import FreeSimpleGUI as sg

layout = [
    [sg.Text("Hola mundo Python"), sg.Button("Cerrar")],
    [sg.Input(key="Nombre")],
    [sg.Checkbox("Aceptar términos", key="t_y_c")],
    [sg.Radio("Opción A", "Grupo1", key="a"), sg.Radio("Opción B", "Grupo1", key="b")],
    [sg.Combo(["Rojo", "Verde", "Azul"], key="COLOR")],
    [sg.Slider(range=(1, 100), orientation="h", key="SLIDER")],
    [sg.Multiline(size=(30, 5), key="COMENTARIO")]
]

window = sg.Window("Ejemplo01 - Mi primer GUI", layout, font=('Arial', 20))

window.read()
window.close()

