palabras = ["Hola","Mundo","Python","Programacion"]
for palabrasV in palabras:
    print(f"La palabra '{palabrasV}' tiene {palabrasV.count("a")} apariciones de A, {palabrasV.count("e")} apariciones de E, {palabrasV.count("i")} apariciones de I, {palabrasV.count("o")} apariciones de O y {palabrasV.count("u")} apariciones de U")